import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';

function Login({ setIsAuthenticated, apiUrl }) {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`${apiUrl}/login`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(formData)
            });

            const data = await response.json();

            if (response.ok) {
                localStorage.setItem('token', data.token); // Zapisz token
                setIsAuthenticated(true); // Zaktualizuj stan autoryzacji
                navigate('/movies'); // Przekieruj na /movies
            } else {
                setError(data.error || 'Błąd logowania');
                console.error('Błąd logowania:', data.error);
            }
        } catch (err) {
            setError('Wystąpił błąd podczas logowania');
            console.error('Błąd:', err);
        }
    };

    return (
        <div>
            <h2>Logowanie</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label>Hasło:</label>
                    <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type="submit">Zaloguj</button>
            </form>
            <p>
                Nie masz konta? <a href="/register">Zarejestruj się</a>
            </p>
        </div>
    );
}

export default Login;
