import React, { useState } from 'react';
import './App.css';

function Login() {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('');
        setError('');

        try {
            const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
            const response = await fetch(`${apiUrl}/login`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    email: formData.email,
                    password: formData.password
                })
            });

            if (!response.ok) {
                const errorData = await response.text();
                throw new Error(`Błąd HTTP: ${response.status}, ${errorData}`);
            }

            const data = await response.json();
            localStorage.setItem('token', data.token); // Zapisz token JWT
            setMessage('Logowanie zakończone sukcesem!');
            setFormData({
                email: '',
                password: ''
            });
            // Opcjonalnie: Przekieruj na stronę główną
            // window.location.href = '/';
        } catch (err) {
            setError(`Błąd logowania: ${err.message}`);
            console.error('Szczegóły błędu:', err);
        }
    };

    return (
        <div className="login-page">
            <div className="center">
                <h1 className="formTitle">Logowanie</h1>
                {message && <p className="success-message">{message}</p>}
                {error && <p className="error-message">{error}</p>}
                <form onSubmit={handleSubmit}>
                    <input
                        type="email"
                        name="email"
                        className="email"
                        placeholder="Email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="password"
                        name="password"
                        className="password"
                        placeholder="Hasło"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="submit"
                        className="submitButtonLogin"
                        value="Zaloguj"
                    />
                </form>
            </div>
        </div>
    );
}

export default Login;
