import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Settings, CircleUserRound, Filter } from 'lucide-react'; // Zastąpiono Funnel na Filter
import './App.css';

const Header = ({ searchQuery, setSearchQuery }) => {
    const navigate = useNavigate();

    const handleLogout = () => {
        localStorage.removeItem('access_token');
        navigate('/login');
        window.location.reload(); // Odświeżamy stronę, aby zresetować stan
    };

    return (
        <header className="header-container">
            <div className="header-left">
                <div className="logo">
                    <a href="/">
                        <img src="/blackeaglelogo.png" alt="logo" />
                    </a>
                </div>
                <nav className="nav-links">
                    <Link to="/movies">Filmy</Link>
                    <Link to="/series">Seriale</Link>
                    <Link to="/watched">Obejrzane</Link>
                </nav>
            </div>

            <div className="header-right">
                <input
                    type="text"
                    className="search-header"
                    placeholder="Szukaj"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
                <div className="Header_icons">
                    <Filter className="icon-funnel" /> {/* Zastąpiono Funnel na Filter */}
                    <Settings className="icon-settings" />
                    <button onClick={handleLogout} className="logout-button" title="Wyloguj się">
                        <CircleUserRound className="icon-CircleUserRound" />
                    </button>
                </div>
            </div>
        </header>
    );
};

export default Header;
