import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './login';
import Register from './register';
import Movies from './movies';
import './App.css';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setIsAuthenticated(true);
        }
    }, []);

    return (
        <div className="App">
            <Router>
                <Routes>
                    <Route
                        path="/"
                        element={<Navigate to="/login" />}
                    />
                    <Route
                        path="/login"
                        element={<Login setIsAuthenticated={setIsAuthenticated} apiUrl={apiUrl} />}
                    />
                    <Route
                        path="/register"
                        element={<Register setIsAuthenticated={setIsAuthenticated} apiUrl={apiUrl} />}
                    />
                    <Route
                        path="/movies"
                        element={isAuthenticated ? <Movies filteredMovies={Movies} /> : <Navigate to="/login" />}
                    />
                </Routes>
            </Router>
        </div>
    );
}

export default App;
