import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './Login'; // Poprawiona nazwa pliku
import Register from './Register';
import Movies from './Movies';
import Series from './Series';
import Watched from './Watched';
import MovieDetails from './MovieDetails';
import Home from './Home';
import Header from './Header';
import Footer from './Footer';
import './App.css';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [movies, setMovies] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    //const apiUrl = 'https://backend-g7rx.onrender.com';
    const apiUrl = 'http://localhost:5000';

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setIsAuthenticated(true);
            // Pobierz filmy
            const fetchMovies = async () => {
                try {
                    const response = await fetch(`${apiUrl}/movies`, {
                        method: 'GET',
                        headers: {
                            'Content-Type': 'application/json',
                            Authorization: `Bearer ${token}`,
                        },
                    });
                    if (!response.ok) {
                        throw new Error('B��d pobierania film�w');
                    }
                    const data = await response.json();
                    setMovies(data);
                } catch (err) {
                    console.error('B��d pobierania film�w:', err);
                    setMovies([]);
                }
            };
            fetchMovies();
        }
    }, [apiUrl]);

    // Filtruj filmy na podstawie searchQuery
    const filteredMovies = movies.filter((movie) =>
        movie.title?.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="App">
            <Router>
                {isAuthenticated && <Header searchQuery={searchQuery} setSearchQuery={setSearchQuery} />}
                <Routes>
                    <Route path="/" element={<Navigate to="/home" />} />
                    <Route
                        path="/login"
                        element={<Login setIsAuthenticated={setIsAuthenticated} apiUrl={apiUrl} />}
                    />
                    <Route
                        path="/register"
                        element={<Register setIsAuthenticated={setIsAuthenticated} apiUrl={apiUrl} />}
                    />
                    <Route
                        path="/home"
                        element={
                            isAuthenticated ? <Home filteredMovies={filteredMovies} /> : <Navigate to="/login" />
                        }
                    />
                    <Route
                        path="/movies"
                        element={
                            isAuthenticated ? <Movies filteredMovies={filteredMovies} /> : <Navigate to="/login" />
                        }
                    />
                    <Route
                        path="/series"
                        element={
                            isAuthenticated ? <Series filteredMovies={filteredMovies} /> : <Navigate to="/login" />
                        }
                    />
                    <Route
                        path="/watched"
                        element={
                            isAuthenticated ? <Watched filteredMovies={filteredMovies} /> : <Navigate to="/login" />
                        }
                    />
                    <Route
                        path="/movie/:id"
                        element={
                            isAuthenticated ? <MovieDetails apiUrl={apiUrl} /> : <Navigate to="/login" />
                        }
                    />
                </Routes>
                <Footer />
            </Router>
        </div>
    );
}

export default App;
