import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Register({ setIsAuthenticated, apiUrl }) {
    const [formData, setFormData] = useState({
        first_name: '',
        last_name: '',
        email: '',
        username: '',
        password: ''
    });
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`${apiUrl}/register`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(formData)
            });

            const data = await response.json();

            if (response.ok) {
                // Po rejestracji automatycznie logujemy użytkownika
                const loginResponse = await fetch(`${apiUrl}/login`, {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        email: formData.email,
                        password: formData.password
                    })
                });

                const loginData = await loginResponse.json();

                if (loginResponse.ok) {
                    localStorage.setItem('token', loginData.token); // Zapisz token
                    setIsAuthenticated(true); // Zaktualizuj stan autoryzacji
                    navigate('/movies'); // Przekieruj na /movies
                } else {
                    setError('Rejestracja udana, ale nie udało się zalogować');
                }
            } else {
                setError(data.error || 'Błąd rejestracji');
                console.error('Błąd rejestracji:', data.error);
            }
        } catch (err) {
            setError('Wystąpił błąd podczas rejestracji');
            console.error('Błąd:', err);
        }
    };

    return (
        <div className="register-page">
            <div className="center">
                <div class="login_logo">
                    <a href="/">
                        <img src="blackeaglelogo.png" alt="logo" className="logo-image" />
                    </a>
                </div>
                <h1 className="formTitle">Rejestracja</h1>
                {error && <p className="error-message">{error}</p>}
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        name="first_name"
                        className="imie"
                        placeholder="Imię"
                        value={formData.first_name}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="last_name"
                        className="nazwisko"
                        placeholder="Nazwisko"
                        value={formData.last_name}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="text"
                        name="username"
                        className="login"
                        placeholder="Login"
                        value={formData.username}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="email"
                        name="email"
                        className="email"
                        placeholder="Email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="password"
                        name="password"
                        className="password"
                        placeholder="Hasło"
                        value={formData.password}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="password"
                        name="confirmPassword"
                        className="password"
                        placeholder="Powtórz Hasło"
                        value={formData.confirmPassword}
                        onChange={handleChange}
                        required
                    />
                    <input
                        type="submit"
                        className="submitButtonLogin"
                        value="Zarejestruj"
                    />
                </form>
            </div>
        </div>
    );
}

export default Register;
