import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './login';
import Register from './register';
import Movies from './movies'; // Zak�adam, �e masz komponent Movies.js
import './App.css';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const apiUrl = process.env.REACT_APP_API_URL;

    // Sprawd�, czy u�ytkownik jest zalogowany (np. poprzez token w localStorage)
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setIsAuthenticated(true);
        }
    }, []);

    // Komponent chroni�cy trasy
    const ProtectedRoute = ({ children }) => {
        return isAuthenticated ? children : <Navigate to="/login" />;
    };

    return (
        <Router>
            <div className="App">
                <Routes>
                    {/* Przekierowanie z / na /login */}
                    <Route path="/" element={<Navigate to="/login" />} />
                    <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} apiUrl={apiUrl} />} />
                    <Route path="/register" element={<Register setIsAuthenticated={setIsAuthenticated} apiUrl={apiUrl} />} />
                    {/* Chroniona trasa /movies */}
                    <Route
                        path="/movies"
                        element={
                            <ProtectedRoute>
                                <Movies apiUrl={apiUrl} />
                            </ProtectedRoute>
                        }
                    />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
