import React, { useState, useEffect } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { EffectCoverflow, Navigation, Autoplay } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/navigation';
import { Link } from 'react-router-dom';
import './App.css';

function Home({ filteredMovies, apiUrl }) {
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        if (filteredMovies.length > 0) {
            setLoading(false);
        }
    }, [filteredMovies]);

    const seriesList = filteredMovies.filter((movie) => movie.movie_type === 'series');

    if (loading) return <p style={{ color: '#f5c518', textAlign: 'center' }}>Ładowanie...</p>;

    return (
        <div className="movie-slider">
            {filteredMovies.length > 0 ? (
                <>
                    <Swiper
                        loop={true}
                        autoHeight={true}
                        slidesPerView={3}
                        spaceBetween={10}
                        breakpoints={{
                            320: { slidesPerView: 2, spaceBetween: 20 },
                            480: { slidesPerView: 3, spaceBetween: 30 },
                            640: { slidesPerView: 4, spaceBetween: 40 },
                        }}
                        centeredSlides={true}
                        effect="coverflow"
                        coverflowEffect={{
                            rotate: 0,
                            slideShadows: false,
                            depth: 300,
                            stretch: 0,
                        }}
                        autoplay={{
                            delay: 3000,
                            disableOnInteraction: false,
                        }}
                        modules={[EffectCoverflow, Navigation, Autoplay]}
                        className="coverflowSwiper"
                    >
                        {filteredMovies.map((movie) => (
                            <SwiperSlide key={`slide-${movie.movie_id}`}>
                                <div className="carousel-item">
                                    <Link to={`/movie/${movie.movie_id}`} className="carousel-item">
                                        <img
                                            src={movie.poster || 'https://placehold.co/200x300'}
                                            alt={movie.title || 'Brak tytułu'}
                                            className="carousel-poster"
                                        />
                                        <h3 className="carousel-title">{movie.title || 'Brak tytułu'}</h3>
                                        <p className="carousel-meta">{movie.prod_year || 'Brak roku'}</p>
                                    </Link>
                                </div>
                            </SwiperSlide>
                        ))}
                    </Swiper>

                    <div className="categories-slider">
                        <div className="category-section">
                            <h2 className="category-title">Filmy</h2>
                            <Swiper
                                loop={true}
                                spaceBetween={15}
                                slidesPerView={8}
                                modules={[Navigation]}
                                navigation
                            >
                                {filteredMovies
                                    .filter((movie) => movie.movie_type === 'movie')
                                    .map((movie) => (
                                        <SwiperSlide key={movie.movie_id}>
                                            <Link to={`/movie/${movie.movie_id}`} className="movie-card">
                                                <img
                                                    src={movie.poster || 'https://placehold.co/200x300'}
                                                    alt={movie.title || 'Brak tytułu'}
                                                    className="movie-poster"
                                                />
                                                <h3 className="movieTitle">{movie.title || 'Brak tytułu'}</h3>
                                            </Link>
                                        </SwiperSlide>
                                    ))}
                            </Swiper>
                        </div>
                    </div>

                    <div className="categories-slider">
                        <div className="category-section">
                            <h2 className="category-title">Seriale</h2>
                            <Swiper
                                loop={true}
                                spaceBetween={15}
                                slidesPerView={8}
                                modules={[Navigation]}
                                navigation
                            >
                                {seriesList.map((movie) => (
                                    <SwiperSlide key={movie.movie_id}>
                                        <Link to={`/movie/${movie.movie_id}`} className="movie-card">
                                            <img
                                                src={movie.poster || 'https://placehold.co/200x300'}
                                                alt={movie.title || 'Brak tytułu'}
                                                className="movie-poster"
                                            />
                                            <h3 className="movieTitle">{movie.title || 'Brak tytułu'}</h3>
                                        </Link>
                                    </SwiperSlide>
                                ))}
                            </Swiper>
                        </div>
                    </div>
                </>
            ) : (
                <p style={{ color: '#f5c518', textAlign: 'center' }}>Brak filmów do wyświetlenia</p>
            )}
        </div>
    );
}

export default Home;
