import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await axios.post('http://localhost:5000/login', {
        email, // Upewnij się, że pole 'email' jest wysyłane
        password, // Upewnij się, że pole 'password' jest wysyłane
      }, {
        headers: { 'Content-Type': 'application/json' },
      });

      if (response.status === 200) {
        // Przechowaj token lub zidentyfikuj użytkownika (zależnie od Twojej logiki)
        const { access_token } = response.data;
        localStorage.setItem('token', access_token); // Przykładowe przechowywanie tokena
        navigate('/'); // Przekierowanie po zalogowaniu
      }
    } catch (err) {
      setError(err.response?.data?.error || 'An error occurred');
      console.error('Login error:', err.response?.data);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
