import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './App.css';

function Movies({ apiUrl }) {
    const [movies, setMovies] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Brak tokenu autoryzacji');
                    setLoading(false);
                    return;
                }
                const response = await fetch(`${apiUrl}/movies`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        Authorization: `Bearer ${token}`,
                    },
                });
                if (!response.ok) {
                    throw new Error('B��d pobierania film�w');
                }
                const data = await response.json();
                setMovies(data);
            } catch (err) {
                setError('Wyst�pi� b��d podczas pobierania film�w');
                console.error('B��d:', err);
            } finally {
                setLoading(false);
            }
        };
        fetchMovies();
    }, [apiUrl]);

    const movieList = Array.isArray(movies)
        ? movies.filter(movie => movie.movie_type?.toLowerCase() === 'movie')
        : [];

    return (
        <div className="movies-page">
            <h1 className="movies-title">Filmy</h1>
            {loading ? (
                <p style={{ color: '#f5c518', textAlign: 'center' }}>�adowanie...</p>
            ) : error ? (
                <p style={{ color: '#f5c518', textAlign: 'center' }}>{error}</p>
            ) : movieList.length === 0 ? (
                <p style={{ color: '#f5c518', textAlign: 'center' }}>
                    Brak film�w do wy�wietlenia.
                </p>
            ) : (
                <div className="movies-grid">
                    {movieList.map((movie) => (
                        <Link
                            to={`/movie/${movie.movie_id}`}
                            className="movies-card"
                            key={movie.movie_id}
                        >
                            <div className="movies-poster-wrapper">
                                <img
                                    className="movies-poster"
                                    src={movie.poster || 'https://via.placeholder.com/150x220'}
                                    alt={movie.title || 'Brak tytu�u'}
                                    onError={(e) => {
                                        e.target.src = 'https://via.placeholder.com/150x220';
                                    }}
                                />
                            </div>
                            <div className="movies-info">
                                <h2 className="movies-name">{movie.title || 'Brak tytu�u'}</h2>
                            </div>
                        </Link>
                    ))}
                </div>
            )}
        </div>
    );
}

export default Movies;
