import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Login({ setIsAuthenticated, apiUrl }) {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            console.log('apiUrl:', apiUrl);
            const response = await fetch(`${apiUrl}/login`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(formData)
            });
            console.log('Response status:', response.status);
            if (!response.ok) {
                const text = await response.text();
                console.log('Response text:', text);
                setError(`Błąd serwera: ${response.status} ${text}`);
                return;
            }
            const data = await response.json();
            if (response.ok) {
                localStorage.setItem('token', data.token);
                setIsAuthenticated(true);
                navigate('/movies');
            } else {
                setError(data.error || 'Błąd logowania');
                console.error('Błąd logowania:', data.error);
            }
        } catch (err) {
            setError('Wystąpił błąd podczas logowania');
            console.error('Błąd:', err);
        }
    };

    return (
        <div className="login-page">
            <div className="center">
                <div className="login_logo">
                    <img src="/img/logo.png" alt="Logo" className="logo-image" />
                </div>
                <h2 className="formTitle">Logowanie</h2>
                {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}
                <form onSubmit={handleSubmit}>
                    <div>
                        <input
                            type="email"
                            name="email"
                            className="email"
                            placeholder="Email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div>
                        <input
                            type="password"
                            name="password"
                            className="password"
                            placeholder="Hasło"
                            value={formData.password}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <button type="submit" className="submitButtonLogin">
                        Zaloguj
                    </button>
                </form>
                <p className="linkreg">
                    Nie masz konta?{' '}
                    <a href="/register" className="text_register_acc">
                        <span className="text_register_acc_butt">Zarejestruj się</span>
                    </a>
                </p>
            </div>
        </div>
    );
}

export default Login;
