import React, { useState, useEffect } from 'react';
import jwtDecode from 'jwt-decode'; // Do dekodowania tokenu JWT

function Movies({ apiUrl }) {
    const [movies, setMovies] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) {
                    setError('Brak tokenu autoryzacji');
                    return;
                }

                // Dekoduj token, aby uzyska� user_id
                const decoded = jwtDecode(token);
                const userId = decoded.user_id;

                const response = await fetch(`${apiUrl}/movies?user_id=${userId}`, {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        Authorization: `Bearer ${token}`
                    }
                });

                const data = await response.json();

                if (response.ok) {
                    setMovies(data);
                } else {
                    setError(data.error || 'B��d pobierania film�w');
                }
            } catch (err) {
                setError('Wyst�pi� b��d podczas pobierania film�w');
                console.error('B��d:', err);
            }
        };

        fetchMovies();
    }, [apiUrl]);

    return (
        <div>
            <h2>Filmy</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {movies.length === 0 && !error && <p>Brak film�w do wy�wietlenia</p>}
            <ul>
                {movies.map((movie) => (
                    <li key={movie.movie_id}>
                        {movie.title} ({movie.releaseyear})
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Movies;
