import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import Watched from './watched';
import Header from './header';
import Footer from './footer';
import Home from './home';
import Register from './register';
import Login from './login';
import Movies from './movies';
import Series from './series';
import MovieDetails from './moviedetails';
import './App.css';

function AppWrapper() {
    const location = useLocation();
    const [movies, setMovies] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchMovies = async () => {
            try {
                const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';
                const response = await fetch(`${apiUrl}/login`, {
                    headers: {
                        'Content-Type': 'application/json',
                        // Je�li endpoint wymaga tokenu, dodaj po zalogowaniu:
                        // 'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                const data = await response.json();
                setMovies(data);
            } catch (err) {
                console.error('B��d pobierania film�w:', err);
                setError('Nie uda�o si� pobra� listy film�w. Spr�buj ponownie p�niej.');
            }
        };
        fetchMovies();
    }, []);

    const filteredMovies = movies.filter((movie) =>
        movie.title.toLowerCase().includes(searchQuery.toLowerCase())
    );

    const hideHeaderFooter = location.pathname === '/register' || location.pathname === '/login';

    return (
        <>
            {!hideHeaderFooter && (
                <Header
                    searchQuery={searchQuery}
                    setSearchQuery={setSearchQuery}
                />
            )}
            {error && <p className="error-message">{error}</p>}
            <Routes>
                <Route path="/" element={<Home filteredMovies={filteredMovies} />} />
                <Route path="/watched" element={<Watched filteredMovies={filteredMovies} />} />
                <Route path="/movies" element={<Movies filteredMovies={filteredMovies} />} />
                <Route path="/series" element={<Series filteredMovies={filteredMovies} />} />
                <Route path="/register" element={<Register />} />
                <Route path="/login" element={<Login />} />
                <Route path="/movie/:id" element={<MovieDetails />} />
            </Routes>
            {!hideHeaderFooter && <Footer />}
        </>
    );
}

function App() {
    return (
        <Router>
            <AppWrapper />
        </Router>
    );
}

export default App;
