import React from 'react';
import { Link } from 'react-router-dom';
import './Movies.css'; // Import styl�w, je�li s� w osobnym pliku

function Movies({ filteredMovies = [] }) {
    // Sprawd�, czy filteredMovies istnieje i jest tablic�
    const movieList = Array.isArray(filteredMovies)
        ? filteredMovies.filter(movie => movie.movie_type === 'movie')
        : [];

    // Debugowanie danych wej�ciowych
    console.log('filteredMovies:', filteredMovies);
    console.log('movieList:', movieList);

    return (
        <div className="movies-page">
            <h1 className="movies-title">Filmy</h1>
            {movieList.length === 0 ? (
                <p style={{ color: '#f5c518', textAlign: 'center' }}>
                    Brak film�w do wy�wietlenia.
                </p>
            ) : (
                <div className="movies-grid">
                    {movieList.map((movie, i) => (
                        <Link
                            to={`/movie/${movie.movie_id}`}
                            className="movies-card"
                            key={movie.movie_id || i} // U�yj movie_id jako klucza, je�li dost�pne
                        >
                            <div className="movies-poster-wrapper">
                                <img
                                    className="movies-poster"
                                    src={movie.poster || 'https://via.placeholder.com/150x220'}
                                    alt={movie.title || 'Brak tytu�u'}
                                    onError={(e) => {
                                        e.target.src = 'https://via.placeholder.com/150x220'; // Zapasowy obrazek
                                    }}
                                />
                            </div>
                            <div className="movies-info">
                                <h2 className="movies-name">{movie.title || 'Brak tytu�u'}</h2>
                            </div>
                        </Link>
                    ))}
                </div>
            )}
        </div>
    );
}

export default Movies;
